import {useEffect,useState} from 'react'
import { Timer } from '../App/Types/timer-types'
import '../Pages/draft-styles.css'
import { useWebSocket } from 'react-use-websocket/dist/lib/use-websocket'
import { ReadyState } from 'react-use-websocket'
import { BASE_URL } from '../App/Slices/baseurl'
import { DraftList } from '../App/Types/champ-select-types'

export const CountdownTimer = () => { 
    const [time, setTime] = useState<Timer>({minutes:1,seconds:0})
    ///const [reset, setReset] = useState(false)
    const [timer, setTimer] = useState({target:0});

    const tick = () => {
      if (time.minutes === 0 && time.seconds === 0) {
        setTime({minutes: 0, seconds: 0})
      }
      else if (time.seconds===0) {
        setTime({minutes: time.minutes-1, seconds: 59})
      }
      else {
        setTime({minutes: time.minutes, seconds: time.seconds-1})
      }
    }

    useEffect(()=>{
      setTimer({target:Date.now()+60000})
    },[])

    setInterval(() => {
      if(timer.target>Date.now()) {
        const timerId = setInterval(() => tick(), 1000);
        return () => clearInterval(timerId)
      }
    }, 1000);
  
    
    const {sendMessage} = useWebSocket(BASE_URL,{
      onMessage: ()=> (message:WebSocketEventMap['message']) => {
        const response:DraftList = JSON.parse(message.data)
        setTime(response.timer)
      },
      share:true, ///maybe share should be false
      retryOnError: true,
      shouldReconnect: () => true
    })

   /* useEffect(() => {

    })*/

    ///handle reset needs to send a command back to the server telling it to reset the timer
    const handleReset = () => {if(time!==null){setTime({minutes: time.minutes, seconds: time.seconds})}}


   if (time!==null) {return (
      <div className='timer'>
        <p>{`${time.minutes.toString().padStart(2,'0')}:${time.seconds.toString().padStart(2, '0')}`}</p> 
      </div>
    )}
    else{return(<div className='timer'></div>)}
  }
