import {useEffect,useState} from 'react'
import { Timer, DraftList, DraftRequest, isTimer} from '../App/Types/champ-select-types'
import '../Pages/draft-styles.css'
import { useWebSocket } from 'react-use-websocket/dist/lib/use-websocket'
import { BASE_URL } from '../App/Slices/baseurl'

export const CountdownTimer = () => { 
  const [time, setTime] = useState<Timer|null>(null)
  
  const {sendMessage} = useWebSocket(BASE_URL, {
    onOpen: () => console.log('connection opened'),
    onClose: () => console.log('connection closed'),
    onMessage: (message:WebSocketEventMap['message']) => {
      let timer:Timer = JSON.parse(message.data)
      ///console.log(message)
      console.log(message.data)
      console.log(timer)
      /*might need if statement so this only runs if the timer differs from the requestBody.
      Alternatively make it so timer only sends a restart message back and forth.
      keep the actual timer entirely frontend*/
      if(isTimer(timer)){
        setTime(timer)
      }
    },

    share:true, ///maybe share should be false
    retryOnError: true,
    shouldReconnect: () => true
  })
  
  const handleReset = () => {
    setTime({minutes: 0, seconds: 0})
    sendMessage(JSON.stringify({minutes: 0, seconds: 0}))
  }
  
  const tick = () => {
    if (time!=null){
      if (time.minutes === 0 && time.seconds === 0) {
        setTime({minutes: 0, seconds: 0})
      }
      else if (time.seconds===0) {
        setTime({minutes: time.minutes-1, seconds: 59})
      }
      else {
        setTime({minutes: time.minutes, seconds: time.seconds-1})
      }
    }  

  }

  ///this news effect needs to reset if the target changes I believe putting it in the dependency array does this
  ///connect thing to handleConfirm to reset the timer
  useEffect(() => {
    if(time?.minutes!=undefined){
      handleReset()
    }
    else{ 
      const timerId = setInterval(() => tick(), 1000);
      return () => clearInterval(timerId)
    }
  },[time])
  
  if(time?.minutes!=undefined){
    return (
      <div className='timer'>
        <p>{`${time.minutes.toString().padStart(2, '0')}:${time.seconds.toString().padStart(2, '0')}`}</p> 
      </div>
    )
  }
  else {
    return (
      <></>
    )
  }
  
}
