import {useEffect,useState} from 'react'
import { Timer } from '../App/Types/timer-types'
import '../Pages/draft-styles.css'
import { useWebSocket } from 'react-use-websocket/dist/lib/use-websocket'
import { BASE_URL } from '../App/Slices/baseurl'

export const CountdownTimer = ({minutes,seconds}:Timer) => { 
    const [time, setTime] = useState<Timer>({minutes,seconds})
    useWebSocket(BASE_URL,{
      onMessage:(e)=> setTime(e.data.timer)
    })


    const handleReset = () => {setTime({minutes: time.minutes, seconds: time.seconds})}
    ///it is possible I may need to either move the timer state into each component individually or I need to use redux to manage state
    
    useEffect(()=>{
      if (reset===true) {handleReset()}
    },[reset])
   
    const tick = () => {
      if (time.minutes === 0 && time.seconds === 0) {
        setTime({reset:reset, minutes: 0, seconds: 0})
      }
      else if (time.seconds===0) {
        setTime({reset:reset, minutes: time.minutes-1, seconds: 59})
      }
      else {
        setTime({reset:reset, minutes: time.minutes, seconds: time.seconds-1})
      }
    }

    useEffect(() => {
      const timerId = setInterval(() => tick(), 1000);
      ///what does clear/set Interval do
      return () => clearInterval(timerId)
    })

    return (
      <div className='timer'>
        <p>{`${time.minutes.toString().padStart(2, '0')}:${time.seconds.toString().padStart(2, '0')}`}</p> 
      </div>
    )
  }
